    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // login code here
        if(models.UsersModel.verifyUser(usernametext.getText(), passwordtext.getText())) {
            javax.swing.JOptionPane.showMessageDialog(null, "Login Success!", "Login", javax.swing.JOptionPane.INFORMATION_MESSAGE);
            mainWindow m = new mainWindow(UsersModel.getUser(usernametext.getText()));
            m.setVisible(true);
            this.dispose();
        } else {
            javax.swing.JOptionPane.showMessageDialog(null, "Wrong user/pass!", "Login", javax.swing.JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jLabel5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel5MouseClicked
            // open create account window
            registerWindow r = new registerWindow();
            r.setVisible(true);
            this.dispose();
    }//GEN-LAST:event_jLabel5MouseClicked

